create table meeting(
	id int auto_increment primary key,
    meeting_name varchar(100) not null,
    schedule_date_time datetime not null,
    created_by varchar(50) not null,
    createdOn datetime,
    meeting_location varchar(100),
    ward_id integer not null,
    meeting_attendance	boolean default false,
    status boolean default true,
    constraint FK_meetingWard foreign key (ward_id) references KOTTAYAM_WARD(ID),
    constraint FK_MeetingCreator foreign key (created_by) references user_details(userid)
);

select * from meeting;

create table meeting_update_info(
	id int auto_increment primary key,
    meeting_id int not null,
    updated_by varchar(50) not null,
    updated_time datetime,
    constraint FK_MeetingUpdater foreign key (updated_by) references user_details(userid),
    constraint FK_meetingInfo foreign key (meeting_id) references meeting(id)
);

select * from meeting_update_info;

create table meeting_attendance_details(
	id int auto_increment primary key,
    meeting_id int not null,
    userid varchar(50) not null,
    attendance boolean default false,
    constraint FK_MeetingParticipant foreign key (userid) references user_details(userid),
    constraint FK_MeetingDetails foreign key (meeting_id) references meeting(id)
);

select * from meeting_attendance_details;
select ma.meeting_id,ud.userid,ud.first_name,ud.last_name,ud.phone_no,ud.ward_name,ma.attendance from user_details ud join meeting_attendance_details ma  on ud.userid =ma.userid;

